{"changed":false,"filter":false,"title":"User.php","tooltip":"/app/User.php","value":"<?php\n\nnamespace App;\n\nuse Illuminate\\Notifications\\Notifiable;\nuse Illuminate\\Foundation\\Auth\\User as Authenticatable;\n\nclass User extends Authenticatable\n{\n    use Notifiable;\n\n    /**\n     * The attributes that are mass assignable.\n     *\n     * @var array\n     */\n    protected $fillable = [\n        'name', 'email', 'password',\n    ];\n\n    /**\n     * The attributes that should be hidden for arrays.\n     *\n     * @var array\n     */\n    protected $hidden = [\n        'password', 'remember_token',\n    ];\n    protected $casts = [\n        'is_active'\n    ];\n    public function setPasswordAttribute($value)\n    {\n        $this->attributes['password'] = bcrypt($value);\n    }\n    public function role()\n    {\n        return $this->belongsTo('App\\Models\\Role');\n    }\n    public function getIsActiveAttribute()\n    {\n        if($this->attributes['is_active'])\n        {\n            return \"Active\";\n        }\n        else\n        {\n            return \"Inactive\";\n        }\n    }\n}\n","undoManager":{"mark":-1,"position":-1,"stack":[]},"ace":{"folds":[],"scrolltop":75,"scrollleft":0,"selection":{"start":{"row":31,"column":4},"end":{"row":34,"column":5},"isBackwards":true},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":4,"state":"php-start","mode":"ace/mode/php"}},"timestamp":1512908124647}